// Importation de la bibliothèque React, de la fonction useState et des composants AddTask et Task.
import React, { useState } from 'react'; 
import AddTask from './components/AddTask'; 
import Task from './components/Task'; 

// Définition de la fonction composant principale de l'application.
function App() { 

    // Création d'un état initial pour la liste de tâches.
    const [tasks, setTasks] = useState([ 
        {
            id: 1,
            text: 'Faire les courses',
        },
        {
            id: 2,
            text: 'Jouer à la play',
        },
        {
            id: 3,
            text: 'Sortir le chien',
        },
    ]);

    /**
     * 
     * Définition d'une fonction pour ajouter une nouvelle tâche à la liste.
     * 
     * Génère un identifiant unique pour la nouvelle tâche.
     * Crée un nouvel objet tâche avec l'identifiant et la description passés en paramètre.
     * Met à jour l'état de la liste de tâches en ajoutant la nouvelle tâche.
     * 
     */
    const addTask = (task) => { 
        const id = Math.floor(Math.random() * 10000) + 1;
        const newTask = { id, ...task };
        setTasks([...tasks, newTask]);
    };

    /**
     * 
     * Définition d'une fonction pour supprimer une tâche de la liste.
     * Met à jour l'état de la liste de tâches en supprimant la tâche 
     * avec l'identifiant passé en paramètre.
     * 
     * */ 
    const deleteTask = (id) => { 
        setTasks(tasks.filter((task) => task.id !== id));
    };

    /**
     * 
     * Défintion d'une fonction pour éditer une tâche dans la liste.
     * Met à jour l'état de la liste de tâche en remplaçant la tâche
     * avec l'identifiant passé en paramètre par une nouvelle tâche
     */
    const editTask = (id, upadtedTask) => {
        setTasks(tasks.map((task) => {
            if (task.id === id) {
                return { ...task,  text: updatedTask };
            }
            return task;
        }));
    };

    /**
     * 
     * Rendu de l'interface utilisateur.  
     * 
     * <AddTask onAdd={addTask} /> Permet d'afficher le composant AddTask avec la fonction addTask en tant que props.
     * 
     * Si la liste de tâches n'est pas vide, alors on affiche :
     * les tâches pour chaque tâche dans la liste, avec l'identifiant, la description et la fonction deleteTask en tant que prop.
     * 
     * Sinon, affiche un message indiquant qu'il n'y a pas de tâches.
     * 
     * */ 
    return ( 
        <div className='container'>

            <header className='header'>
                <h1>Ma To-Do List</h1>
                <AddTask onAdd={addTask} /> 
            </header>

            {tasks.length > 0 ? (
                <div className="tasks">
                    {tasks.map((task) => (
                        <div key={task.id} className="task">
                            <Task task={task} onDelete={deleteTask} onEdit={editTask} />
                        </div>
                    ))}
                </div>
            ) : (
                <p className="no-tasks">Pas de tâche</p>
            )}
        </div>
    );
}

// Exporte la fonction composant principale "App" pour pouvoir être utilisée ailleurs dans l'application.
export default App;
