// Cette ligne importe la bibliothèque React dans le module
import React from 'react';

// Cette ligne crée un composant fonctionnel DeleteTask qui accepte onDelete et task en tant que props
const DeleteTask = ({ onDelete, task }) => {

  // Cette ligne retourne un bouton qui appelle la fonction onDelete avec l'ID de la tâche lorsqu'il est cliqué
  return (
    <button className='btn delete-btn' onClick={() => onDelete(task.id)}>Supprimer la tâche</button>
  );
};

// Cette ligne exporte le composant DeleteTask en tant que composant par défaut pour être utilisé ailleurs
export default DeleteTask;
