import React, { useState } from 'react';
import Task from './components/Task';
import AddTask from './components/AddTask';
import EditTask from './components/EditTask';
import DeleteTask from './components/DeleteTask';

function App() { 

    // Création d'un état initial pour la liste de tâches.
    const [tasks, setTasks] = useState([ 
        {
            id: 1,
            text: 'Faire les courses',
        },
        {
            id: 2,
            text: 'Jouer à la play',
        },
        {
            id: 3,
            text: 'Sortir le chien',
        },
    ]);
    const [editTask, setEditTask] = useState(null);
    const [deleteTask, setDeleteTask] = useState(null);

    const addTask = (task) => {
        setTasks([...tasks, task]);
    };

    const updateTask = (updatedTask) => {
        const updatedTasks = tasks.map((task) =>
            task.id === updatedTask.id ? updatedTask : task
        );
        setTasks(updatedTasks);
        setEditTask(null);
    };

    const deleteTaskById = (taskId) => {
        const updatedTasks = tasks.filter((task) => task.id !== taskId);
        setTasks(updatedTasks);
        setDeleteTask(null);
    };

    return (
        <div className="App">
            <h1>To-Do List</h1>
            <AddTask addTask={addTask} />
            {tasks.map((task) => (
                <Task key={task.id} task={task} setEditTask={setEditTask} setDeleteTask={setDeleteTask} />
            ))}
            {editTask && <EditTask task={editTask} updateTask={updateTask} />}
            {deleteTask && (
                <DeleteTask task={deleteTask} deleteTaskById={deleteTaskById} />
            )}
        </div>
    );
}

export default App;
