// Importation de la bibliothèque React et de la fonction useState
import React, { useState } from 'react';

// Création d'un composant AddTask qui accepte onAdd en tant que prop
const AddTask = ({ onAdd }) => {

  // Utilisation de la fonction useState pour initialiser le texte de la tâche à une chaîne vide et créer une fonction setText pour mettre à jour la valeur
  const [text, setText] = useState('');

  // Création d'une fonction onSubmit qui est appelée lorsque le formulaire est soumis
    const onSubmit = (e) => {
        e.preventDefault();
        if (!text) {
            alert('Please add a task');
            return;
        }
        onAdd({ text });
        setText('');
    };

    // Retourne un formulaire avec un champ de texte pour ajouter une tâche et un bouton pour soumettre le formulaire
    return (
        <form className='add-form' onSubmit={onSubmit}>
            <div className='form-control'>
                <input type='text' placeholder='Add Task' value={text} onChange={(e) => setText(e.target.value)}/>
                <button className='btn'>Ajouter la tâche</button>
            </div>
        </form>
    );
};

// Cette ligne exporte le composant AddTask en tant que composant par défaut pour être utilisé ailleurs
export default AddTask;
