// Importation de la bibliothèque React et du composant DeleteTask.
import React from 'react';
import DeleteTask from './DeleteTask';
import EditTask from './EditTask';

/**
 * 
 * Création d'un composant Task qui accepte task et onDelete en tant que props
 * Il permet de retourner un élément HTML contenant le texte de la tâche et le composant DeleteTask pour supprimer la tâche
 * 
 */
const Task = ({ task, onDelete }) => {
    const [isEditing, setIsEditing] = useState(false);
    const handleEdit = (updatedTask) => {
    onEdit(updatedTask);
    setIsEditing(false);
    };

    return (
        <div className='task'>
            <h3>{task.text} <DeleteTask onDelete={onDelete} task={task}/></h3>
        </div>
    );
};

// Exporte le composant Task pour être utilisé ailleurs
export default Task;
