// Importataion de la bibliothèque React.
import React from 'react';

// Création d'un composant DeleteTask qui accepte onDelete et task en tant que props
const DeleteTask = ({ onDelete, task }) => {

    // Retourne un bouton qui appelle la fonction onDelete avec l'ID de la tâche lorsqu'il est cliqué
    return (
        <button className='btn delete-btn' onClick={() => onDelete(task.id)}>Supprimer la tâche</button>
    );
};

// Exporte le composant DeleteTask pour être utilisé ailleurs
export default DeleteTask;
