/**
 * Composant fonctionnel Task représentant une tâche dans une liste de tâches.
 * 
 * Fonctionnalité :
 * Le composant retourne un élément HTML contenant le texte de la tâche et un composant DeleteTask permettant de supprimer la tâche. 
 * Lorsque l'utilisateur clique sur le bouton de suppression de tâche, 
 * la fonction onDelete est appelée avec l'id de la tâche à supprimer.
 * La fonctionnalité principale du composant Task est donc d'afficher les informations d'une tâche et de permettre sa suppression.
 * 
 * --------------------------------------------------
 * 
 * Importation : 
 * - React : Module principal de React.
 * - DeleteTask : Composant React qui est exporté à partir du fichier './DeleteTask.js'.
 * 
 * --------------------------------------------------
 * 
 * Props :
 * - task : objet contenant les informations de la tâche.
 * - onDelete : fonction appelée lorsque l'utilisateur souhaite supprimer la tâche.
 *
 * --------------------------------------------------
 * 
 * Puis exportation du composant Task afin être utilisé ailleurs.
 */ 

import React, { useState } from 'react';
import { FaTimes, FaEdit } from 'react-icons/fa';
import DeleteTask from './DeleteTask';
import EditTask from './EditTask';

const Task = ({ task, onDelete, onEdit }) => {
  const [showEditForm, setShowEditForm] = useState(false);

  return (
    <div className={`task ${task.reminder ? 'reminder' : ''}`} onDoubleClick={() => onEdit(task)}>
      <h3>
        {task.text}
        <FaEdit style={{ color: 'blue', cursor: 'pointer' }} onClick={() => setShowEditForm(!showEditForm)} />
        <FaTimes style={{ color: 'red', cursor: 'pointer' }} onClick={() => onDelete(task.id)} />
      </h3>
      {showEditForm ? (
            <EditTask task={task} onEdit={onEdit} onCancel={() => setShowEditForm(false)} />
        ) : (
            <p>{task.day}</p>
        )}
      <p>{task.day}</p>
    </div>
  );
};  

export default Task;
