import React from 'react';
import DeleteTask from './DeleteTask';
import TaskCompleted from './TaskCompleted';

const Task = ({ task, onDelete, onComplete }) => {
  return (
    <div className='task'>
      <h3>
        {task.text}{' '}
        {!task.completed && (
          <>
            <DeleteTask onDelete={onDelete} task={task} />{' '}
            <TaskCompleted onComplete={onComplete} task={task} />
          </>
        )}
      </h3>
    </div>
  );
};

export default Task;
