// Importation de la bibliothèque React et du composant DeleteTask.
import React from 'react';
import DeleteTask from './DeleteTask';
import EditTask from './EditTask';

/**
 * 
 * Création d'un composant fonctionnel Task qui accepte task et onDelete en tant que props
 * Il permet de retourner un élément HTML contenant le texte de la tâche et le composant DeleteTask pour supprimer la tâche
 * 
 */
const Task = ({ task, onDelete, onEdit }) => {

    const handleEditClick = () => {
        setIsEditing(true);
    };

    const handleCancelClick = () => {
        setIsEditing(false);
    };

    const handleDeleteClick = () => {
        onDelete(task.id);
    };

    const handleEditSubmit = (editedTask) => {
        onEdit(editedTask);
        setIsEditing(false);
    };

    return (
        <div className='task'>
            {isEditing ? (
                <EditTask task={task} onEdit={handleEditSubmit} />
            ) : (
                <div>
                    <h3>{task.text}</h3>
                    <div className='buttons'>
                        <button className='btn edit-btn' onClick={handleEditClick}>
                        Modifier
                        </button>
                        <DeleteTask onDelete={handleDeleteClick} />
                    </div>
                </div>
            )}
        </div>
    );
};

// Exporte le composant Task pour être utilisé ailleurs
export default Task;
